def sort_by_parity(nums):
    left = 0
    right = len(nums) - 1

    while left < right:
        if nums[left] % 2 > nums[right] % 2:
            nums[left], nums[right] = nums[right], nums[left]

        if nums[left] % 2 == 0:
            left += 1
        if nums[right] % 2 == 1:
            right -= 1

